import liff from '@line/liff';
import * as i0 from "@angular/core";
export class CarrerComponent {
    constructor() { }
    async getUserProfile() {
        let profile = await liff.getProfile();
        pictureUrl.src = profile.pictureUrl;
        userId.innerHTML = '<b>UserID:</b>' + profile.userId;
        displayName.innerHTML = '<b>Display Name: </b>' + profile.displayName;
        statusMessage.innerHTML = '<b>Status : </b>' + profile.statusMessage;
        email.innerHTML = '<b>Email : </b>' + liff.getDecodedIDToken().email;
    }
    async main() {
        liff.ready.then(() => {
            if (liff.getOS() === 'android') {
                appDiv.style.backgroundColor = '#888';
            }
            if (liff.isInClient()) {
                this.getUserProfile();
            }
        });
        await liff.init({ liffId: '1656955187-j6JWxVQG' });
    }
    ngOnInit() {
        let appDiv = document.getElementById('body');
        let pictureUrl = document.getElementById('pictureUrl');
        let userId = document.getElementById('userId');
        let displayName = document.getElementById('displayName');
        let statusMessage = document.getElementById('statusMessage');
        let email = document.getElementById('email');
        this.main();
    }
}
